    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        boolean isAdmin=true;
        String id=null;
        trabajador worker = null;
        Admin admin = null;
        
        ArrayList <String> userData = new <String> ArrayList(); 
        
        archivos ar = new archivos();
        
        ArrayList <String> usersArch = new <String>ArrayList();
        usersArch.addAll(ar.archivos(true));
        
        if(usersArch.contains(Jid.getText()+".txt")){
            id = Jid.getText();
            userData.addAll(obtenerCredenciales(Jid.getText(), true));
            worker = new trabajador(id, userData);
            
        }else{
            JOptionPane.showMessageDialog(null, "Acceso denegado:\n"
                            + "\nPor favor ingrese un usuario y/o contrase単a correctos", "Acceso denegado",
                            JOptionPane.ERROR_MESSAGE);
        }
        
        
        char clave[]=jPassword.getPassword();
        String clavedef=new String(clave);

        if (id.equals(Jid.getText()) && clavedef.equals(worker.pasword) &&  worker.puesto.equals("Admin")){
                    this.dispose();   
                    admin= new Admin(id, userData, worker);
                    TareasAdmin tareas = new TareasAdmin(true);
                    tareas.setVisible(true);
            }else if(id.equals(Jid.getText()) && clavedef.equals(worker.pasword) &&  worker.puesto.equals("worker")){
                    this.dispose();
                    TareasTrabajador tareas = new TareasTrabajador(Jid.getText());
                    tareas.setVisible(true);
            }else{

                    JOptionPane.showMessageDialog(null, "Acceso denegado:\n"
                            + "Por favor ingrese un usuario y/o contrase単a correctos", "Acceso denegado",
                            JOptionPane.ERROR_MESSAGE);
            }
    }//GEN-LAST:event_jButton2ActionPerformed

